mapper.select:
SELECT
-- 级联表的字段 ,fangjian.fangjian_name as fangjianName ,fangjian.fangjian_types as fangjianTypes ,fangjian.fangjian_address as fangjianAddress ,fangjian.fangjian_peizhi as fangjianPeizhi ,fangjian.fangjian_photo as fangjianPhoto ,fangjian.fangjian_new_money as fangjianNewMoney ,fangjian.status_types as statusTypes ,fangjian.fangjian_content as fangjianContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.new_money as newMoney ,yonghu.yonghu_delete as yonghuDelete
FROM fangjian_liuyan a left JOIN fangjian fangjian ON a.fangjian_id = fangjian.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.fangjian_id = #{params.fangjianId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.fangjian_liuyan_text like CONCAT('%',#{params.fangjianLiuyanText},'%')
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.fangjian_id IS NOT NULL
and fangjian.fangjian_name like CONCAT('%',#{params.fangjianName},'%')
and fangjian.fangjian_types = #{params.fangjianTypes}
and fangjian.fangjian_address like CONCAT('%',#{params.fangjianAddress},'%')
and fangjian.fangjian_peizhi like CONCAT('%',#{params.fangjianPeizhi},'%')
and fangjian.fangjian_new_money >= #{params.fangjianNewMoneyStart}
and fangjian.fangjian_new_money <= #{params.fangjianNewMoneyEnd}
and fangjian.status_types = #{params.statusTypes}
and fangjian.fangjian_content like CONCAT('%',#{params.fangjianContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
